)sub) res group by tipoCuenta, unidad_ejecutora, nombre_corto union all select sum(saldo_calculado) saldo, res.tipoCuenta, decode(res.unidad_ejecutora,'901','0901','902','0902',res.unidad_ejecutora) unidad_ejecutora, res.nombre_corto, 1 bandera from (select saldo_calculado, unidad_ejecutora,num_cuenta, decode(sub.unidad_ejecutora,901,'Tesorera',902,'Contabilidad Institucional',102,'Contralora Interna', 105,'D.G. Estadsticas Sociodemogrficas',106,'D.G. Estadsticas Econmicas', 107,'D.G. Geografa y Medio Ambiente',108,'D.G. Servicio Pblico de Informacin', 109,'D.G. de Administracin',sub.nombre_corto) nombre_corto, decode(tipo_cuenta,6,1,1,2,2,3,tipo_cuenta) tipoCuenta from (select saldo_calculado,fecha, num_cuenta, decode(num_cuenta,'08400029068',6,'43710149845',1,'08400029076',1,'08400029084',2, '08400029106',1,'08400029114',1,cb.id_tipo_cta ) tipo_cuenta, cb.id_tipo_cta, decode(num_cuenta,'08400029068','901','43710149845','901','08400029076','901','08400029084','901','08400029092','901', '08400029122','901','08400029106','901','08400029114','901', '08400030457','901','08400029386','901', cb.unidad_ejecutora ) unidad_ejecutora, nombre_corto from rf_tr_saldos_diarios sd,rf_tr_cuentas_bancarias cb, sia_admin.RH_TC_UNI_EJECUTORAS ue where fecha = to_date(':fechaFinal','yyyy-mm-dd') and cb.unidad_ejecutora = ue.unidad_ejecutora and sd.id_cuenta = cb.id_cuenta and (cb.id_cuenta in (:idCuentas)) and ((vigencia_desde <= to_date(':fechaInicial','yyyy-mm-dd') AND (vigencia_hasta BETWEEN to_date(':fechaInicial','yyyy-mm-dd') AND to_date(':fechaFinal','yyyy-mm-dd')) OR vigencia_hasta IS NULL) OR (vigencia_desde <= to_date(':fechaInicial','yyyy-mm-dd') AND (vigencia_hasta >= to_date(':fechaFinal','yyyy-mm-dd')) OR vigencia_hasta IS NULL) OR (vigencia_desde BETWEEN to_date(':fechaInicial','yyyy-mm-dd') AND to_date(':fechaFinal','yyyy-mm-dd') AND (vigencia_hasta >= to_date(':fechaFinal','yyyy-mm-dd')) OR vigencia_hasta IS NULL) OR (vigencia_desde BETWEEN to_date(':fechaInicial','yy