select m.fecha_hora, m.unidad, m.unidad_ejecutora,m.monto, com.credito, com.debito from (select fecha_hora, unidad, unidad_ejecutora, sum(decode(valor,1,abs(monto)/0.018,0)) credito, sum(decode(valor,2,abs(monto)/0.013,0)) debito from ( select fecha_hora, c.monto, substr(trim(c.referencia),0,7) || ' - ' || ue.descripcion || ' ' || tpv.descripcion unidad, ue.unidad_ejecutora, case when c.referencia like '%COMISION TAR CRED%' then 1 when c.referencia like '%COMISION TAR DEBIT%' then 2 end valor from rf_tr_movimientos_cuenta c , rf_tc_cuentas_tpv tpv, rf_tc_unidades_ejecutoras_tece ue where fecha_hora >= to_date (':fechaInicial','yyyy-mm-dd') AND fecha_hora < to_date (':fechaFinal','yyyy-mm-dd')+1 and id_clave_trans_recla =324 and tpv.referencia_tpv = substr(trim(c.referencia),0,7) and tpv.unidad_ejecutora in (select distinct c.unidad_ejecutora from rf_tr_cuentas_bancarias c where c.id_cuenta in (:idCuentas)) and ue.UNIDAD_EJECUTORA = tpv.UNIDAD_EJECUTORA and ue.entidad = tpv.entidad and ue.ambito = tpv.ambito
) group by fecha_hora, unidad, unidad_ejecutora) com, (select fecha_hora, sum(c.monto) monto, substr(trim(c.referencia),0,7) || ' - ' || ue.descripcion || ' ' || tpv.descripcion unidad, ue.unidad_ejecutora from rf_tr_movimientos_cuenta c , rf_tc_cuentas_tpv tpv, rf_tc_unidades_ejecutoras_tece ue where fecha_hora >= to_date (':fechaInicial','yyyy-mm-dd') AND fecha_hora < to_date (':fechaFinal','yyyy-mm-dd')+1 and id_clave_trans_recla = 634 and tpv.referencia_tpv = substr(trim(c.referencia),0,7) and tpv.unidad_ejecutora in (select distinct c.unidad_ejecutora from rf_tr_cuentas_bancarias c where c.id_cuenta in (:idCuentas)) and ue.UNIDAD_EJECUTORA = tpv.UNIDAD_EJECUTORA and ue.entidad = tpv.entidad and ue.ambito = tpv.ambito
group by fecha_hora,ue.unidad_ejecutora , substr(trim(c.referencia),0,7) || ' - ' || ue.descripcion || ' ' || tpv.descripcion ) m where com.FECHA_HORA = m.fecha_hora and com.unidad = m.unidad and com.unidad_ejecutora = m.unidad_ejecutora order by m.unidad, m.unidad_ejecutora,m.fecha_hora

